  "content": "#!/usr/bin/env node
/**
 * WhatsApp Web Monitor for Claude Code
 * Monitors a specific group chat and processes new messages
 */

const fs = require('fs');
const path = require('path');

const STATE_FILE = path.join(__dirname, '.whatsapp-state.json');

// Load or initialize state
function loadState() {
  try {
    return JSON.parse(fs.readFileSync(STATE_FILE, 'utf8'));
  } catch {
    return { lastMessageTimestamp: null, lastMessageText: null };
  }
}

function saveState(state) {
  fs.writeFileSync(STATE_FILE, JSON.stringify(state, null, 2));
}

// Extract messages from WhatsApp Web DOM
// This runs in the browser context
function extractMessagesScript() {
  return `
    (() => {
      const messages = [];
      const messageElements = document.querySelectorAll('[data-testid="msg-container"]');

      messageElements.forEach((el, index) => {
        // Get sender name
        const senderEl = el.querySelector('[data-testid="msg-meta"] span') ||
                        el.querySelector('.copyable-text[data-pre-plain-text]');
        let sender = 'Unknown';
        if (senderEl) {
          const preText = senderEl.getAttribute('data-pre-plain-text');
          if (preText) {
            const match = preText.match(/\\[(.*?)\\]\s*(.*?):/);
            sender = match ? match[2] : 'Unknown';
          }
        }

        // Get message text
        const textEl = el.querySelector('.selectable-text span');
        const text = textEl ? textEl.innerText : '';

        // Get timestamp
        const timeEl = el.querySelector('[data-testid="msg-meta"] span');
        const timestamp = timeEl ? timeEl.innerText : '';

        // Check if it's an outgoing message (from you)
        const isOutgoing = el.classList.contains('message-out') ||
                          el.closest('.message-out') !== null;

        if (text.trim()) {
          messages.push({
            sender,
            text,
            timestamp,
            isOutgoing,
            index
          });
        }
      });

      return messages.slice(-20); // Last 20 messages
    })()
  `;
}

// Find group chat in the list
function findGroupScript(groupName) {
  return `
    (() => {
      const chats = document.querySelectorAll('[data-testid="chat-list"] [role="listitem"]');
      for (const chat of chats) {
        const titleEl = chat.querySelector('[data-testid="conversation-info-header"] span') ||
                       chat.querySelector('.zoWT4');
        if (titleEl && titleEl.innerText.toLowerCase().includes('${groupName.toLowerCase()}')) {
          return { found: true, title: titleEl.innerText };
        }
      }
      return { found: false };
    })()
  `;
}

// Check for new messages indicator
function hasNewMessagesScript() {
  return `
    (() => {
      const unreadBadge = document.querySelector('[data-testid="icon-unread-count"]');
      const unreadText = document.querySelector('[data-testid="msg-unread"]');
      return {
        hasUnread: !!(unreadBadge || unreadText),
        unreadCount: unreadBadge ? unreadBadge.innerText : null
      };
    })()
  `;
}

module.exports = {
  loadState,
  saveState,
  extractMessagesScript,
  findGroupScript,
  hasNewMessagesScript,
  STATE_FILE
};
"
}

