{
  "name": "task-agent",
  "files": [
    {
      "name": "index.ts",
      "content": "/**
 * Continuous Task Agent
 */

import { createClient } from 'https://esm.sh/@supabase/supabase-js@2.39.0';

const CONFIG = {
  ANALYSIS_BATCH_SIZE: 5,
  MIN_TASK_AGE_MINUTES: 0
};

const state = { consecutiveErrors: 0 };

Deno.serve(async (req) => {
  try {
    const supabaseUrl = Deno.env.get('SUPABASE_URL');
    const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY');
    const supabase = createClient(supabaseUrl!, supabaseServiceKey!);

    // Get tasks with no autonomous subtasks
    const { data: tasksWithSubtasks } = await supabase
      .from('autonomous_subtasks')
      .select('parent_task_id');

    const taskIdsWithSubtasks = new Set(tasksWithSubtasks?.map(t => t.parent_task_id) || []);

    const { data: tasks, error } = await supabase
      .from('deep_work_tasks')
      .select('*')
      .order('created_at', { ascending: false })
      .limit(10);

    if (error) throw error;

    // Filter tasks without subtasks
    const tasksNeedingAnalysis = (tasks || []).filter(t => !taskIdsWithSubtasks.has(t.id));
    const results: any[] = [];

    for (const task of tasksNeedingAnalysis.slice(0, CONFIG.ANALYSIS_BATCH_SIZE)) {
      // Create planner subtask
      const { data: planner, error: plannerError } = await supabase
        .from('autonomous_subtasks')
        .insert({
          parent_task_id: task.id,
          title: `Plan: ${task.title}`,
          description: 'Analyze and create execution plan',
          status: 'pending',
          priority: 'medium',
          assigned_agent_role: 'planner',
          verification_required: false,
          agent_metadata: { analyzed_at: new Date().toISOString() }
        })
        .select()
        .single();

      if (!plannerError && planner) {
        results.push({ taskId: task.id, plannerId: planner.id });
      }
    }

    // Execute pending planners
    const { data: pendingPlanners } = await supabase
      .from('autonomous_subtasks')
      .select('*')
      .eq('status', 'pending')
      .eq('assigned_agent_role', 'planner')
      .limit(3);

    for (const planner of (pendingPlanners || [])) {
      // Mark as executing
      await supabase
        .from('autonomous_subtasks')
        .update({ status: 'executing', started_at: new Date().toISOString() })
        .eq('id', planner.id);

      // Get parent task
      const { data: parentTask } = await supabase
        .from('deep_work_tasks')
        .select('*')
        .eq('id', planner.parent_task_id)
        .single();

      // Create execution subtasks
      const subtasks = [
        {
          parent_task_id: planner.parent_task_id,
          title: `Research: ${parentTask?.title || 'task'}`,
          description: 'Research existing solutions',
          status: 'pending',
          priority: 'medium',
          assigned_agent_role: 'researcher',
          verification_required: false,
          estimated_duration_min: 30
        },
        {
          parent_task_id: planner.parent_task_id,
          title: `Implement: ${parentTask?.title || 'task'}`,
          description: 'Implement the solution',
          status: 'pending',
          priority: 'high',
          assigned_agent_role: 'coder',
          verification_required: true,
          estimated_duration_min: 60
        }
      ];

      const { data: created } = await supabase
        .from('autonomous_subtasks')
        .insert(subtasks)
        .select();

      // Mark planner complete
      await supabase
        .from('autonomous_subtasks')
        .update({
          status: 'completed',
          completed_at: new Date().toISOString(),
          summary: `Created ${created?.length || 0} subtasks`
        })
        .eq('id', planner.id);

      results.push({ plannerId: planner.id, created: created?.length || 0 });
    }

    return new Response(
      JSON.stringify({ status: 'success', tasks_analyzed: tasksNeedingAnalysis.length, results }),
      { status: 200, headers: { 'Content-Type': 'application/json' } }
    );

  } catch (error) {
    const errorMsg = error instanceof Error ? error.message : String(error);
    return new Response(
      JSON.stringify({ status: 'error', error: errorMsg }),
      { status: 500, headers: { 'Content-Type': 'application/json' } }
    );
  }
});"
    }
  ]
}

