  "content": "#!/usr/bin/env python3
"""
WhatsApp Web Monitor - Automated monitoring for group chats
Usage: python3 whatsapp-monitor.py "Group Name"
"""

import asyncio
import json
import sys
import os
from datetime import datetime
from playwright.async_api import async_playwright

STATE_FILE = os.path.join(os.path.dirname(__file__), '.whatsapp-state.json')


def load_state():
    """Load last known message state"""
    try:
        with open(STATE_FILE, 'r') as f:
            return json.load(f)
    except:
        return {"last_check": None, "last_messages": []}


def save_state(state):
    """Save current state"""
    with open(STATE_FILE, 'w') as f:
        json.dump(state, f, indent=2)


async def extract_messages(page):
    """Extract messages from WhatsApp Web"""
    messages = await page.evaluate("""() => {
        const msgs = [];
        const elements = document.querySelectorAll('[data-testid="msg-container"]');

        elements.forEach(el => {
            // Get sender
            const senderEl = el.querySelector('span[dir="auto"][title]');
            let sender = senderEl ? senderEl.getAttribute('title') : 'You';

            // Get text
            let text = '';
            const textSelectors = [
                '.selectable-text span',
                '[data-testid="msg-text"] span',
                '.copyable-text .selectable-text'
            ];
            for (const sel of textSelectors) {
                const textEl = el.querySelector(sel);
                if (textEl) {
                    text = textEl.innerText;
                    break;
                }
            }

            // Get timestamp
            const timeEl = el.querySelector('[data-testid="msg-meta"] span');
            const timestamp = timeEl ? timeEl.innerText : '';

            // Check if outgoing
            const isOutgoing = el.classList.contains('message-out') ||
                              el.closest('.message-out') !== null;

            if (text.trim()) {
                msgs.push({
                    sender: isOutgoing ? 'You' : sender,
                    text: text.trim(),
                    timestamp,
                    isOutgoing
                });
            }
        });

        return msgs;
    }""")
    return messages


async def find_and_open_chat(page, group_name):
    """Find and click on the group chat"""
    print(f"🔍 Looking for group: {group_name}")

    # Search for the chat
    await page.fill('[data-testid="chat-list-search"]', group_name)
    await asyncio.sleep(1)

    # Click on the chat
    chat_selector = f'[data-testid="chat-list"] span[title="{group_name}"]'
    try:
        await page.click(chat_selector)
        print(f"✅ Opened chat: {group_name}")
        await asyncio.sleep(2)
        return True
    except:
        # Try partial match
        chats = await page.query_selector_all('[data-testid="chat-list"] [role="listitem"]')
        for chat in chats:
            title_el = await chat.query_selector('span[title]')
            if title_el:
                title = await title_el.get_attribute('title')
                if group_name.lower() in title.lower():
                    await chat.click()
                    print(f"✅ Opened chat: {title}")
                    await asyncio.sleep(2)
                    return True

        print(f"❌ Could not find group: {group_name}")
        return False


async def monitor_whatsapp(group_name):
    """Main monitoring function"""
    state = load_state()

    async with async_playwright() as p:
        # Launch browser
        browser = await p.chromium.launch(headless=False, slow_mo=50)
        context = await browser.new_context(viewport={'width': 1280, 'height': 800})
        page = await context.new_page()

        print("🌐 Opening WhatsApp Web...")
        await page.goto('https://web.whatsapp.com')

        # Wait for QR scan or login
        print("⏳ Waiting for login...")
        print("📱 Please scan the QR code with your phone")

        try:
            # Wait for chat list to appear (means logged in)
            await page.wait_for_selector('[data-testid="chat-list"]', timeout=120000)
            print("✅ Logged in successfully!")
        except:
            print("❌ Timeout waiting for login. Please try again.")
            await browser.close()
            return

        # Find and open the group
        if not await find_and_open_chat(page, group_name):
            await browser.close()
            return

        # Extract messages
        print("📨 Extracting messages...")
        messages = await extract_messages(page)

        # Get group name from page
        group_title = await page.evaluate("""() => {
            const el = document.querySelector('header [data-testid="conversation-info-header"] span');
            return el ? el.innerText : 'Unknown';
        }""")

        # Find new messages
        last_texts = [m['text'] for m in state.get('last_messages', [])]
        new_messages = [m for m in messages if m['text'] not in last_texts]

        result = {
            "group": group_title,
            "checked_at": datetime.now().isoformat(),
            "total_messages": len(messages),
            "new_messages": len(new_messages),
            "messages": messages[-20:],  # Last 20
            "new": new_messages
        }

        # Save state
        state['last_check'] = datetime.now().isoformat()
        state['last_messages'] = messages[-10:]  # Keep last 10 for comparison
        save_state(state)

        # Output results
        print("\n" + "="*50)
        print(f"📊 Results for: {group_title}")
        print("="*50)
        print(json.dumps(result, indent=2))

        # Keep browser open for a bit so user can see
        print("\n⏳ Keeping browser open for 10 seconds...")
        await asyncio.sleep(10)

        await browser.close()
        return result


if __name__ == "__main__":
    group_name = sys.argv[1] if len(sys.argv) > 1 else "SISO: Lumelle"
    result = asyncio.run(monitor_whatsapp(group_name))

    # Save results for Claude to analyze
    output_file = os.path.join(os.path.dirname(__file__), 'last-messages.json')
    with open(output_file, 'w') as f:
        json.dump(result, f, indent=2)
    print(f"\n💾 Results saved to: {output_file}")
"
}

