### 2.2 Edge Function: Agent Service

```typescript
// supabase/functions/agent/index.ts
import { serve } from 'https://deno.land/std@0.168.0/http/server.ts';
import { Anthropic } from 'https://esm.sh/@anthropic-ai/sdk@0.24.0';

interface AgentRequest {
  message: string;
  orgId: string;
  userId: string;
  context?: Record<string, unknown>;
  conversationId?: string;
}

serve(async (req) => {
  const { message, orgId, userId, context, conversationId } = await req.json() as AgentRequest;

  // Initialize Claude with MCP tools
  const anthropic = new Anthropic({ apiKey: Deno.env.get('ANTHROPIC_API_KEY') });

  // Tools scoped to tenant
  const tools = [
    createTaskTool(orgId),
    queryDataTool(orgId),
    updateProductTool(orgId),
    getAnalyticsTool(orgId)
  ];

  const response = await anthropic.messages.create({
    model: 'claude-3-5-sonnet-20241022',
    max_tokens: 4096,
    tools,
    messages: [
      { role: 'system', content: getSystemPrompt(orgId) },
      { role: 'user', content: message }
    ]
  });

  return new Response(JSON.stringify(response));
});
```

### 2.3 MCP Tools (Tenant-Scoped)

```typescript
// supabase/functions/agent/tools/create-task.ts
export const createTaskTool = (orgId: string) => ({
  name: 'create_task',
  description: 'Create a new task for the team',
  input_schema: {
    type: 'object',
    properties: {
      title: { type: 'string' },
      description: { type: 'string' },
      priority: { type: 'string', enum: ['low', 'medium', 'high'] },
      category: { type: 'string', enum: ['content', 'design', 'development', 'marketing'] }
    },
    required: ['title', 'description']
  },
  handler: async (input: any) => {
    const { data, error } = await supabase
      .from('tasks')
      .insert({
        ...input,
        org_id: orgId,  // Auto-injected, not from LLM
        status: 'pending_approval',  // Goes to HITL pipeline
        created_at: new Date().toISOString()
      })
      .select()
      .single();

    if (error) throw error;
    return { taskId: data.id, status: 'pending_approval' };
  }
});
```

